import { render } from "@testing-library/react";
import React, { useState, useEffect} from "react";
import { getStorage, ref, uploadBytes, listAll, getDownloadURL } from "firebase/storage";
// import UploadImages from "./upload";
// import App from "./upload";
import { onAuthStateChanged } from "firebase/auth";
import { auth } from "../firebase-config";

function Category(props) {

    const [user, setUser] = useState({});
    const [image, setImage] = useState({});

    const storage = getStorage();

    const [listUrl, setUrls] = useState([]);

    onAuthStateChanged(auth, (currentUser) => {
        setUser(currentUser);
    })

    function handleChange(e) {
      if (e.target.files.length) {
        setImage(e.target.files[0]);
      }
    }

    async function handleUpload(e) {

      const path = `${user.uid}/${props.name}/${Date().toLocaleString()}`
      const storageRef = ref(storage, path);

      uploadBytes(storageRef, image).then((snapshot) => {
        console.log('Uploaded a blob or file!');
        getImage()
      });
    }

    function getImage() {
      const listRef = ref(storage, `${user.id}/${props.name}`);
      listAll(listRef)
        .then((res) => {
          res.items.forEach((itemRef) => {
            const path = itemRef._location.path_;
            const urlRef = ref(storage, path);
            getDownloadURL(urlRef).then((url) => {
                //update the state
                setUrls(oldArray => [...oldArray, url] );
            });
          });
          }).catch((error) => {
            console.log(error);
          });
    }

    return (
        <>
        <div>
          <input type="file">
            Choose File
          </input>

          <button>
            Upload
          </button>
        </div>

        </>
    )  

}



export default Category;
