import { render } from "@testing-library/react";
import React, { useState, useEffect} from "react";
import { getStorage, ref, uploadBytes, listAll, getDownloadURL } from "firebase/storage";
// import UploadImages from "./upload";
// import App from "./upload";
import { onAuthStateChanged } from "firebase/auth";
import { auth, storage } from "../firebase-config";

function Category(props) {

    const [user, setUser] = useState({});
    const [image, setImage] = useState({});

    const [listUrl, setUrls] = useState([]);

    onAuthStateChanged(auth, (currentUser) => {
        setUser(currentUser);
    })

    function handleChange(e) {

      if (e.target.files.length) {
        setImage(e.target.files[0]);
        console.log(image)
      }
    }

    async function handleUpload(e) {

      const path = `${user.uid}/${props.name}/${Date.now() + ""}`
      const storageRef = ref(storage, path);

      if (image) {
      uploadBytes(storageRef, image).then((snapshot) => {
        console.log('Uploaded a blob or file!');
        getImage()
      }).catch((error) => {
        console.log(error);
      });
    }
  }

    function getImage() {
      const listRef = ref(storage, `${user.uid}/${props.name}`);
      setUrls([]);
      listAll(listRef)
        .then((res) => {
          res.items.forEach((itemRef) => {
            const path = itemRef._location.path_;
            const urlRef = ref(storage, path);
            getDownloadURL(urlRef).then((url) => {
                //update the state
                setUrls(oldArray => [...oldArray, url] );
            });
          });
          }).catch((error) => {
            console.log(error);
          });
    }

    useEffect(() => {
      getImage() 
    }, [user]);

    return (
        <>
        <div class="text-3xl text-center">
          {props.name}
        </div>

        <div class="text-center">
          <input type="file" onChange = {handleChange} >

          </input>

          <button onClick = {handleUpload}>
            Upload
          </button>
        </div>

        <div>
          {
            listUrl.map(url => (
              (
                <div> 
                  <img class="w-60 h-60 group relative" src={url} />
                </div>
              )
            ))
          }
        </div>
        </>
    )  

}



export default Category;
